mapper.delete:
delete from Ticket where ofId in (select ofId from onFlight where fId = #{fId});
delete from Seat where ofId in (select ofId from onFlight where fId = #{fId});
delete from Cabin where ofId in (select ofId from onFlight where fId = #{fId});
delete from onFlight where ofId in (select ofId from (select ofId from PerformFlight where fId = #{fId}) as pf);
delete from Flight where fId = #{fId};
